mapper.select:
SELECT

FROM biz_lead
WHERE deleted = 0

    AND lead_type = #{leadType}


    AND status = #{status}


    AND rating = #{rating}


    AND industry = #{industry}


    AND region = #{region}


    AND (
        title LIKE CONCAT('%', #{keyword}, '%')
        OR description LIKE CONCAT('%', #{keyword}, '%')
        OR company_name LIKE CONCAT('%', #{keyword}, '%')
        OR contact_name LIKE CONCAT('%', #{keyword}, '%')
        OR tags LIKE CONCAT('%', #{keyword}, '%')
    )

ORDER BY 
    is_top DESC,
    is_recommend DESC,
    rating_score DESC,
    create_time DESC

SELECT

FROM biz_lead
WHERE deleted = 0
AND status = 3
AND (
    industry = #{userIndustry}
    OR region = #{userRegion}

        OR (investment_amount BETWEEN #{minInvestment} & #{maxInvestment})

)
AND create_by != #{userId}
ORDER BY 
    CASE WHEN industry = #{userIndustry} THEN 1 ELSE 2 END,
    rating_score DESC,
    view_count DESC,
    create_time DESC
LIMIT #{limit}

SELECT , ( CASE WHEN industry = #{targetIndustry} THEN 30 ELSE 0 END + CASE WHEN region = #{targetRegion} THEN 20 ELSE 0 END + CASE WHEN project_scale = #{targetScale} THEN 15 ELSE 0 END + CASE WHEN company_scale = #{targetCompanyScale} THEN 10 ELSE 0 END + CASE WHEN rating = 'A' THEN 25 WHEN rating = 'B' THEN 20 WHEN rating = 'C' THEN 15 ELSE 10 END ) AS match_score FROM biz_lead WHERE deleted = 0 AND status = 3 AND create_by != #{userId}
AND id NOT IN
#{leadId}
HAVING match_score >= #{minMatchScore} ORDER BY match_score DESC, rating_score DESC LIMIT #{limit}

SELECT
    keyword,
    search_count,
    last_search_time
FROM biz_search_log
WHERE search_date >= #{startDate}
AND search_date <= #{endDate}
GROUP BY keyword
ORDER BY search_count DESC
LIMIT #{limit}

mapper.update:
UPDATE biz_lead SET status = #{status}, update_time = NOW(), update_by = #{updateBy} WHERE id IN
#{leadId}
AND deleted = 0

UPDATE biz_lead SET rating = #{rating}, rating_score = #{ratingScore}, update_time = NOW(), update_by = #{updateBy} WHERE id IN
#{leadId}
AND deleted = 0
